INSERT INTO `wp_wc_admin_notes` VALUES (1,'wc-admin-wc-helper-connection','info','en_US','Connect to WooCommerce.com','Connect to get important product notifications and updates.','{}','unactioned','woocommerce-admin','2020-11-16 06:35:43',NULL,0,'plain','',1,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (2,'wc-admin-onboarding-profiler-reminder','update','en_US','Welcome to WooCommerce! Set up your store and start selling','We\'re here to help you going through the most important steps to get your store up and running.','{}','unactioned','woocommerce-admin','2020-11-16 06:35:43',NULL,0,'plain','',1,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (3,'wc-admin-onboarding-email-marketing','info','en_US','Tips, product updates, and inspiration','We\'re here for you - get tips, product updates and inspiration straight to your email box','{}','unactioned','woocommerce-admin','2020-11-16 06:35:44',NULL,0,'plain','',1,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (4,'wc-admin-marketing-intro','info','en_US','Connect with your audience','Grow your customer base and increase your sales with marketing tools built for WooCommerce.','{}','unactioned','woocommerce-admin','2020-11-16 06:35:44',NULL,0,'plain','',1,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (5,'ecomm-need-help-setting-up-your-store','info','en_US','Need help setting up your Store?','Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.','{}','pending','woocommerce.com','2020-11-16 06:35:44',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (6,'woocommerce-services','info','en_US','WooCommerce Shipping & Tax','WooCommerce Shipping &amp; Tax helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.','{}','pending','woocommerce.com','2020-11-16 06:35:44',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (7,'ecomm-unique-shopping-experience','info','en_US','For a shopping experience as unique as your customers','Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.','{}','pending','woocommerce.com','2020-11-16 06:35:44',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (8,'wc-admin-coupon-page-moved','update','en_US','Coupon management has moved!','Coupons can now be managed from Marketing &gt; Coupons. Click the button below to remove the legacy WooCommerce &gt; Coupons menu item.','{}','actioned','woocommerce-admin','2020-11-16 06:39:17',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (9,'wc-admin-mobile-app','info','en_US','Install Woo mobile app','Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.','{}','unactioned','woocommerce-admin','2020-11-18 17:02:30',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (10,'wcpay-promo-2020-11','marketing','en_US','Manage subscriber payments from your store\'s dashboard','Securely accept cards and manage transactions right from your dashboard with <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Payments</a>  – now supporting <a href=\"https://woocommerce.com/products/woocommerce-subscriptions/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Subscriptions</a>! <br /><br /><em>By clicking \"Install now,\" you agree to our <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">Terms of Service</a>.</em>','{}','pending','woocommerce.com','2020-11-19 23:44:54',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (11,'wcpay-subscriptions-2020-11','marketing','en_US','Manage subscriber payments from your store\'s dashboard','Securely accept cards and manage transactions right from your dashboard with <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Payments</a>  – now supporting <a href=\"https://woocommerce.com/products/woocommerce-subscriptions/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Subscriptions</a>! <br /><br /><em>By clicking \"Install now,\" you agree to our <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">Terms of Service</a>.</em>','{}','pending','woocommerce.com','2020-11-19 23:44:54',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (12,'wc-admin-onboarding-payments-reminder','info','en_US','Start accepting payments on your store!','Take payments with the provider that’s right for you - choose from 100+ payment gateways for WooCommerce.','{}','unactioned','woocommerce-admin','2020-11-21 19:09:08',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (13,'wc-admin-usage-tracking-opt-in','info','en_US','Help WooCommerce improve with usage tracking','Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"https://orbemorder.local/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Read more</a> about what data we collect.','{}','unactioned','woocommerce-admin','2020-11-26 06:20:13',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (14,'wc-admin-woocommerce-payments','marketing','en_US','Try the new way to get paid','Securely accept credit and debit cards on your site. Manage transactions without leaving your WordPress dashboard. Only with <strong>WooCommerce Payments</strong>.<br><br>By clicking \"Get started\", you agree to our <a href=\"https://wordpress.com/tos/\" target=\"_blank\">Terms of Service</a>','{}','unactioned','woocommerce-admin','2020-11-26 06:20:13',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (15,'wc-admin-store-notice-giving-feedback-2','info','en_US','Give feedback','Now that you’ve chosen us as a partner, our goal is to make sure we\'re providing the right tools to meet your needs. We\'re looking forward to having your feedback on the store setup experience so we can improve it in the future.','{}','unactioned','woocommerce-admin','2020-11-26 06:20:13',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (16,'wc-admin-insight-first-sale','survey','en_US','Did you know?','A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?','{}','unactioned','woocommerce-admin','2020-11-26 06:20:13',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (17,'wc-admin-home-screen-feedback','info','en_US','Help us improve the WooCommerce Home screen','We\'d love your input to shape the future of the WooCommerce Home screen together. Feel free to share any feedback, ideas or suggestions that you have.','{}','unactioned','woocommerce-admin','2020-12-05 20:32:08',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (18,'wcpay-promo-2020-12','marketing','en_US','Get 50% off transaction fees with WooCommerce Payments','Keep more of your hard-earned cash by adding <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_exp20\" target=\"_blank\">WooCommerce Payments</a> to your store. Lock in a discounted rate of 1.5% + $0.15 for $25,000 of payments (or three months, whichever comes first). Limited time offer – don’t miss out! <br /><br /><em>By clicking \"Install now,\" you agree to our promotional <a href=\"https://woocommerce.com/terms-conditions/woocommerce-payments-promotion/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_exp20\" target=\"_blank\">Terms of Service</a>.</em>','{}','pending','woocommerce.com','2020-12-21 18:21:08',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (19,'wcpay-subscriptions-2020-12','marketing','en_US','Manage payments from your store\'s dashboard','Securely accept cards and manage transactions right from your dashboard with <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Payments</a>! Zero setup fees or monthly fees. Just pay-as-you-go, starting at just 2.9% + $0.30 per transaction for U.S.-issued cards. <br /><br /><em>By clicking \"Install now,\" you agree to our <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">Terms of Service</a>.</em>','{}','pending','woocommerce.com','2020-12-21 18:21:08',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (20,'wc-admin-getting-started-in-ecommerce','info','en_US','Getting Started in eCommerce - webinar','We want to make eCommerce and this process of getting started as easy as possible for you. Watch this webinar to get tips on how to have our store up and running in a breeze.','{}','pending','woocommerce.com','2021-02-04 18:56:48',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (21,'your-first-product','info','en_US','Your first product','That\'s huge! You\'re well on your way to building a successful online store — now it’s time to think about how you\'ll fulfill your orders.<br /><br />Read our shipping guide to learn best practices and options for putting together your shipping strategy. And for WooCommerce stores in the United States, you can print discounted shipping labels via USPS with <a href=\"https://href.li/?https://woocommerce.com/shipping\" target=\"_blank\">WooCommerce Shipping</a>.','{}','pending','woocommerce.com','2021-02-04 18:56:48',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (22,'wc-admin-real-time-order-alerts','info','en_US','Get real-time order alerts anywhere','Get notifications about store activity, including new orders and product reviews directly on your mobile devices with the Woo app.','{}','unactioned','woocommerce-admin','2021-04-12 16:19:20',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (23,'paypal_ppcp_gtm_2021','marketing','en_US','Offer more options with the new PayPal','Get the latest PayPal extension for a full suite of payment methods with extensive currency and country coverage.','{}','pending','woocommerce.com','2021-04-12 16:19:21',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (24,'facebook_pixel_api_2021','marketing','en_US','Improve the performance of your Facebook ads','Enable Facebook Pixel and Conversions API through the latest version of Facebook for WooCommerce for improved measurement and ad targeting capabilities.','{}','pending','woocommerce.com','2021-04-12 16:19:21',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (25,'facebook_ec_2021','marketing','en_US','Sync your product catalog with Facebook to help boost sales','A single click adds all products to your Facebook Business Page shop. Product changes are automatically synced, with the flexibility to control which products are listed.','{}','pending','woocommerce.com','2021-04-12 16:19:21',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (26,'wc-square-apple-pay-boost-sales','marketing','en_US','Boost sales with Apple Pay','Now that you accept Apple Pay® with Square you can increase conversion rates by letting your customers know that Apple Pay® is available. Here’s a marketing guide to help you get started.','{}','pending','woocommerce.com','2021-04-12 16:19:21',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (27,'wc-square-apple-pay-grow-your-business','marketing','en_US','Grow your business with Square and Apple Pay ','Now more than ever, shoppers want a fast, simple, and secure online checkout experience. Increase conversion rates by letting your customers know that you now accept Apple Pay®.','{}','pending','woocommerce.com','2021-04-12 16:19:21',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (28,'wcpay-apple-pay-is-now-available','marketing','en_US','Apple Pay is now available with WooCommerce Payments!','Increase your conversion rate by offering a fast and secure checkout with <a href=\"https://woocommerce.com/apple-pay/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_applepay\" target=\"_blank\">Apple Pay</a>®. It’s free to get started with <a href=\"https://woocommerce.com/payments/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_applepay\" target=\"_blank\">WooCommerce Payments</a>.','{}','pending','woocommerce.com','2021-04-12 16:19:21',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (29,'wcpay-apple-pay-boost-sales','marketing','en_US','Boost sales with Apple Pay','Now that you accept Apple Pay® with WooCommerce Payments you can increase conversion rates by letting your customers know that Apple Pay® is available. Here’s a marketing guide to help you get started.','{}','pending','woocommerce.com','2021-04-12 16:19:21',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (30,'wcpay-apple-pay-grow-your-business','marketing','en_US','Grow your business with WooCommerce Payments and Apple Pay','Now more than ever, shoppers want a fast, simple, and secure online checkout experience. Increase conversion rates by letting your customers know that you now accept Apple Pay®.','{}','pending','woocommerce.com','2021-04-12 16:19:21',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (31,'wc-admin-optimizing-the-checkout-flow','info','en_US','Optimizing the checkout flow','It\'s crucial to get your store\'s checkout as smooth as possible to avoid losing sales. Let\'s take a look at how you can optimize the checkout experience for your shoppers.','{}','pending','woocommerce.com','2021-04-12 16:19:21',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (32,'wc-admin-first-five-things-to-customize','info','en_US','The first 5 things to customize in your store','Deciding what to start with first is tricky. To help you properly prioritize, we\'ve put together this short list of the first few things you should customize in WooCommerce.','{}','pending','woocommerce.com','2021-04-12 16:19:21',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (33,'wc-payments-qualitative-feedback','info','en_US','WooCommerce Payments setup - let us know what you think','Congrats on enabling WooCommerce Payments for your store. Please share your feedback in this 2 minute survey to help us improve the setup process.','{}','pending','woocommerce.com','2021-05-22 04:30:35',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (34,'share-your-feedback-on-paypal','info','en_US','Share your feedback on PayPal','Share your feedback in this 2 minute survey about how we can make the process of accepting payments more useful for your store.','{}','pending','woocommerce.com','2021-05-22 04:30:35',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (35,'wcpay_instant_deposits_gtm_2021','marketing','en_US','Get paid within minutes – Instant Deposits for WooCommerce Payments','Stay flexible with immediate access to your funds when you need them – including nights, weekends, and holidays. With <a href=\"https://woocommerce.com/products/woocommerce-payments/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_instant_deposits\">WooCommerce Payments\'</a> new Instant Deposits feature, you’re able to transfer your earnings to a debit card within minutes.','{}','pending','woocommerce.com','2021-05-22 04:30:35',NULL,0,'plain','',0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (36,'wc-subscriptions-security-update-3-0-15','info','en_US','WooCommerce Subscriptions security update!','We recently released an important security update to WooCommerce Subscriptions. To ensure your site\'s data is protected, please upgrade <strong>WooCommerce Subscriptions to version 3.0.15</strong> or later.<br /><br />Click the button below to view and update to the latest Subscriptions version, or log in to <a href=\"https://woocommerce.com/my-dashboard\">WooCommerce.com Dashboard</a> and navigate to your <strong>Downloads</strong> page.<br /><br />We recommend always using the latest version of WooCommerce Subscriptions, and other software running on your site, to ensure maximum security.<br /><br />If you have any questions we are here to help — just <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">open a ticket</a>.','{}','pending','woocommerce.com','2021-06-04 20:39:35',NULL,0,'plain','',0,'info');
