INSERT INTO `wp_posts` VALUES (199,1,'2020-11-04 08:05:44','2020-11-04 08:05:44','<!-- wp:group {\"className\":\"map-item gillimank-map-item\"} -->\n<div class=\"wp-block-group map-item gillimank-map-item\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"id\":193,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://orbemorder.local/wp-content/uploads/2020/11/spacer.gif\" alt=\"\" class=\"wp-image-193\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"className\":\"map-item-modal\"} -->\n<p class=\"map-item-modal\">I made it to Gillimank! I should go check out Orbem Academy. Maybe I could peek at Professor Vallorant\'s Homonian book collection.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"map-item pool-map-item\"} -->\n<div class=\"wp-block-group map-item pool-map-item\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"id\":193,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://orbemorder.local/wp-content/uploads/2020/11/spacer.gif\" alt=\"\" class=\"wp-image-193\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"className\":\"map-item-modal\"} -->\n<p class=\"map-item-modal\">Whoa! The pool where Froshile was stuck. I heard he was trying to sell Genie\'s serum off as his own. Hopefully not getting into too much trouble back home.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"map-item spinebeck-map-item\"} -->\n<div class=\"wp-block-group map-item spinebeck-map-item\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"id\":193,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://orbemorder.local/wp-content/uploads/2020/11/spacer.gif\" alt=\"\" class=\"wp-image-193\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"className\":\"map-item-modal\"} -->\n<p class=\"map-item-modal\">Spinebeck Village. I better be careful. These Harilizards can be very ornery. Ever since Ioana and Genie were caught running around here, security has been tight. I would like to practice my Graltinude though.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group -->','Explore','','inherit','closed','closed','','173-revision-v1','','','2020-11-04 08:05:44','2020-11-04 08:05:44','',173,'https://orbemorder.local/173-revision-v1/',0,'revision','',0);
INSERT INTO `wp_posts` VALUES (201,1,'2020-11-07 17:43:07','2020-11-07 17:43:07','<!-- wp:group {\"className\":\"map-item gillimank-map-item\"} -->\n<div class=\"wp-block-group map-item gillimank-map-item\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"id\":193,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://orbemorder.local/wp-content/uploads/2020/11/spacer.gif\" alt=\"\" class=\"wp-image-193\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"className\":\"map-item-modal graeme\"} -->\n<p class=\"map-item-modal graeme\">I made it to Gillimank! I didn\'t spend much time looking around when I visited before. I should go check out Orbem Academy. Maybe I could peek at Professor Vascas\'s Homonian book collection I heard so much about.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"className\":\"map-item-modal sionnah\"} -->\n<p class=\"map-item-modal sionnah\">Ah, it is great to be back home. I can smell the knowledge! I hope I do not run into my horrid niece. I need to make sure my students know I am alive and well. I have missed too many classes since my journey with my earthling friends.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"map-item pool-map-item\"} -->\n<div class=\"wp-block-group map-item pool-map-item\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"id\":193,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://orbemorder.local/wp-content/uploads/2020/11/spacer.gif\" alt=\"\" class=\"wp-image-193\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"className\":\"map-item-modal graeme\"} -->\n<p class=\"map-item-modal graeme\">Whoa! The pool where Froshile was stuck. I heard he was trying to sell Genie\'s serum off as his own. Hopefully not getting into too much trouble back home. Looks like the pool refilled nicely. I can still hear the terrible screams of my friends being attacked by pixies. I should go.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"map-item spinebeck-map-item\"} -->\n<div class=\"wp-block-group map-item spinebeck-map-item\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"id\":193,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://orbemorder.local/wp-content/uploads/2020/11/spacer.gif\" alt=\"\" class=\"wp-image-193\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"className\":\"map-item-modal sionnah\"} -->\n<p class=\"map-item-modal sionnah\">Spinebeck Village. I better be careful. These Harilizards can be very ornery. Ever since Ioana and Genie were caught running around here, security has been tight. I would like to practice my Graltinude though. Maybe I will just pop in for a bit.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group -->','Explore','','inherit','closed','closed','','173-revision-v1','','','2020-11-07 17:43:07','2020-11-07 17:43:07','',173,'https://orbemorder.local/173-revision-v1/',0,'revision','',0);
